// src/pages/Categories.js
import React from "react";
import { Link } from "react-router-dom";
import { Mail, Phone, MapPin, Clock, Shirt, Watch, Headphones, Home, Gamepad2, Car, Book, Dumbbell, Baby } from "lucide-react";
import "./Categories.css";

const Categories = () => {
  const categories = [
    {
      id: 1,
      name: "Electronics",
      description: "Smartphones, laptops, tablets and more",
      icon: Headphones,
      productCount: 245,
      color: "blue",
    },
    {
      id: 2,
      name: "Clothing",
      description: "Fashion for men, women and kids",
      icon: Shirt,
      productCount: 189,
      color: "purple",
    },
    {
      id: 3,
      name: "Home & Garden",
      description: "Furniture, decor and outdoor items",
      icon: Home,
      productCount: 156,
      color: "green",
    },
    {
      id: 4,
      name: "Sports & Fitness",
      description: "Equipment and athletic wear",
      icon: Dumbbell,
      productCount: 134,
      color: "red",
    },
    {
      id: 5,
      name: "Books",
      description: "Fiction, non-fiction and educational",
      icon: Book,
      productCount: 298,
      color: "yellow",
    },
    {
      id: 6,
      name: "Toys & Games",
      description: "Fun for all ages",
      icon: Gamepad2,
      productCount: 87,
      color: "pink",
    },
    {
      id: 7,
      name: "Automotive",
      description: "Car accessories and parts",
      icon: Car,
      productCount: 76,
      color: "gray",
    },
    {
      id: 8,
      name: "Baby & Kids",
      description: "Everything for your little ones",
      icon: Baby,
      productCount: 123,
      color: "orange",
    },
    {
      id: 9,
      name: "Jewelry & Watches",
      description: "Elegant accessories and timepieces",
      icon: Watch,
      productCount: 92,
      color: "indigo",
    }
  ];

  return (
    <div className="categories-page">
      <header className="header">Shop by Category</header>
      <p className="description">
        Discover our wide range of product categories and find exactly what you're looking for.
      </p>

      <div className="grid">
        {categories.map((category) => {
          const Icon = category.icon;
          return (
            <Link
              key={category.id}
              to={`/products?category=${category.name.toLowerCase()}`}
              className="category-card"
            >
              <div className={`icon-circle ${category.color}`}>
                <Icon size={32} />
              </div>
              <h3>{category.name}</h3>
              <p className="text">{category.description}</p>
              <span className="badge">{category.productCount} products</span>
            </Link>
          );
        })}
      </div>

      <div className="banner">
        <h2>Can't find what you're looking for?</h2>
        <p>Browse all our products or use our search feature to find exactly what you need.</p>
        <Link to="/products" className="view-all-btn">
          View All Products
        </Link>
      </div>
    </div>
  );
};

export default Categories;
