import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import { useCart } from '../CartContext';

function Home() {
  const [products, setProducts] = useState([
    { id: 1, name: "Wireless Bluetooth Headphones with Mic - Black", originalPrice: 13999, image: "/images/product5.jpeg", quantity: 1, category: 'Electronics' },
    { id: 2, name: "Smart Watch with Fitness Tracker - Silver", originalPrice: 22499, image: "/images/product6.jpeg", quantity: 1, category: 'Electronics' },
    { id: 3, name: "Portable Bluetooth Speaker - Waterproof", originalPrice: 11199, image: "/images/product7.jpeg", quantity: 1, category: 'Electronics' },
    { id: 4, name: "Wireless Charging Pad - Fast Charging", originalPrice: 8499, image: "/images/product8.jpg", quantity: 1, category: 'Electronics' },
    { id: 5, name: "Laptop Backpack with USB Charging Port", originalPrice: 9899, image: "/images/product9.jpeg", quantity: 1, category: 'Fashion' },
    { id: 6, name: "4K Action Camera with Waterproof Case", originalPrice: 36499, image: "/images/product10.jpeg", quantity: 1, category: 'Electronics' },
    { id: 7, name: "Air Fryer - 5.5L Capacity, Digital Display", originalPrice: 22499, image: "/images/product11.jpeg", quantity: 1, category: 'Home Appliances' },
    { id: 8, name: "Electric Toothbrush with 4 Brush Heads", originalPrice: 19699, image: "/images/product12.jpg", quantity: 1, category: 'Health & Beauty' },
    { id: 9, name: "Noise-Canceling Over-Ear Headphones", originalPrice: 33699, image: "/images/product13.jpeg", quantity: 1, category: 'Electronics' },
    { id: 10, name: "Smart Home Security Camera", originalPrice: 44999, image: "/images/product14.jpeg", quantity: 1, category: 'Electronics' },
    { id: 11, name: "Electric Kettle with Rapid Boil Technology", originalPrice: 8499, image: "/images/product15.jpeg", quantity: 1, category: 'Home Appliances' },
    { id: 12, name: "Smart LED TV - 55 inches", originalPrice: 196999, image: "/images/product16.jpeg", quantity: 1, category: 'Electronics' },
    { id: 13, name: "Cordless Vacuum Cleaner - Powerful Suction", originalPrice: 55999, image: "/images/product17.jpg", quantity: 1, category: 'Home Appliances' },
    { id: 14, name: "Smart Air Purifier with HEPA Filter", originalPrice: 50499, image: "/images/product18.jpg", quantity: 1, category: 'Home Appliances' }
  ]);

  const { addToCart } = useCart();
  const [activeCategory, setActiveCategory] = useState('Electronics');
  const [sortOption, setSortOption] = useState('Most Popular');

  const updateQuantity = (id, change) => {
    setProducts(products.map(product =>
      product.id === id
        ? { ...product, quantity: Math.max(1, product.quantity + change) }
        : product
    ));
  };

  const handleAddToCart = (product) => {
    addToCart(product, product.quantity);
    alert(`${product.name} added to cart!`);
  };

  const getFilteredProducts = () => {
    return products.filter(product => product.category === activeCategory);
  };

  const sortProducts = (productsToSort) => {
    switch (sortOption) {
      case 'Price: Low to High':
        return [...productsToSort].sort((a, b) => a.originalPrice - b.originalPrice);
      case 'Price: High to Low':
        return [...productsToSort].sort((a, b) => b.originalPrice - a.originalPrice);
      case 'Most Popular':
        return [...productsToSort].sort((a, b) => {
          const aPopular = [3,7,12].includes(a.id);
          const bPopular = [3,7,12].includes(b.id);
          return bPopular - aPopular;
        });
      default:
        return productsToSort;
    }
  };

  const filteredProducts = getFilteredProducts();
  const sortedProducts = sortProducts(filteredProducts);
  
  const featuredProducts = sortedProducts.slice(0, 8);
  const bestSellers = sortedProducts.filter(product => [3, 7, 12].includes(product.id));

  const handleCategoryClick = (category) => {
    setActiveCategory(category);
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-PK', {
      style: 'currency',
      currency: 'PKR',
      maximumFractionDigits: 0
    }).format(price);
  };

  return (
    <main className="home-page">
      <div className="banner-container">
        <div className="banner-content">
          <h1 className="banner-title">SUMMER SALE</h1>
          <p className="banner-subtitle">Up to 50% Off Everything!</p>
          <div className="banner-highlights">
            <span className="banner-tag">Limited Time Offer</span>
            <span className="banner-tag">Free Shipping</span>
            <span className="banner-tag">Best Deals</span>
          </div>
          <Link to="/deals">
            <button className="banner-button">Shop Now</button>
          </Link>
        </div>
      </div>

      <div className="deal-categories">
        {['Electronics', 'Home Appliances', 'Fashion', 'Health & Beauty'].map((category) => (
          <button
            key={category}
            className={`category-button ${activeCategory === category ? 'active' : ''}`}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="container">
        <div className="deal-header">
          <h2>{activeCategory}</h2>
          <div className="sort-options">
            <span>Sort by:</span>
            <select value={sortOption} onChange={handleSortChange}>
              <option>Most Popular</option>
              <option>Price: Low to High</option>
              <option>Price: High to Low</option>
            </select>
          </div>
        </div>

        {/* Featured Products Section */}
        <section className="products-section">
          <h2 className="section-title">Featured Products</h2>
          <div className="product-grid">
            {featuredProducts.map(product => (
              <div key={product.id} className="product-box">
                <img src={product.image} alt={product.name} className="product-image" />
                <div className="product-info">
                  <h3 className="product-title">{product.name}</h3>
                  <div className="product-price">{formatPrice(product.originalPrice)}</div>
                  <div className="quantity-controls">
                    <button className="quantity-btn minus" onClick={() => updateQuantity(product.id, -1)}>-</button>
                    <input type="number" className="quantity-input" value={product.quantity} readOnly />
                    <button className="quantity-btn plus" onClick={() => updateQuantity(product.id, 1)}>+</button>
                  </div>
                  <button className="add-to-cart" onClick={() => handleAddToCart(product)}>Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Best Sellers Section */}
        <section className="products-section">
          <h2 className="section-title">Best Sellers</h2>
          <div className="product-grid">
            {bestSellers.map(product => (
              <div key={product.id} className="product-box">
                <img src={product.image} alt={product.name} className="product-image" />
                <div className="product-info">
                  <h3 className="product-title">{product.name}</h3>
                  <div className="product-price">{formatPrice(product.originalPrice)}</div>
                  <div className="quantity-controls">
                    <button className="quantity-btn minus" onClick={() => updateQuantity(product.id, -1)}>-</button>
                    <input type="number" className="quantity-input" value={product.quantity} readOnly />
                    <button className="quantity-btn plus" onClick={() => updateQuantity(product.id, 1)}>+</button>
                  </div>
                  <button className="add-to-cart" onClick={() => handleAddToCart(product)}>Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Newsletter Section */}
        <section className="newsletter-section">
          <h2>Subscribe to Our Newsletter</h2>
          <p>Get the latest updates on new products and upcoming sales</p>
          <form className="newsletter-form">
            <input type="email" placeholder="Your email address" required />
            <button type="submit" className="subscribe-btn">Subscribe</button>
          </form>
        </section>
      </div>
    </main>
  );
}

export default Home;
