import React, { useState } from 'react';
import { ShoppingCart, Search, User, Heart, Menu, X } from "lucide-react";
import { Link, useLocation } from "react-router-dom";
import "./Header.css";

const Header = ({ cartCount, onCartClick }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-container">
        <div className="logo">
          <Link to="/">
            <span className="green">Buy</span><span className="black">Now</span>
          </Link>
        </div>

        <nav className={`nav ${isMenuOpen ? 'mobile-visible' : ''}`}>
          <Link to="/" className={location.pathname === "/" ? "active" : ""} onClick={closeMenu}>Home</Link>
          <Link to="/products" className={location.pathname === "/products" ? "active" : ""} onClick={closeMenu}>Products</Link>
          <Link to="/categories" className={location.pathname === "/categories" ? "active" : ""} onClick={closeMenu}>Categories</Link>
          <Link to="/about" className={location.pathname === "/about" ? "active" : ""} onClick={closeMenu}>About</Link>
          <Link to="/contact" className={location.pathname === "/contact" ? "active" : ""} onClick={closeMenu}>Contact</Link>
        </nav>

        <div className="icons">
          <div className="search-wrapper">
            <Search className="icon search-icon" />
          </div>
          <Link to="/wishlist">
            <Heart className="icon" />
          </Link>
          <Link to="/login">
            <User className="icon" />
          </Link>
          <div className="cart-wrapper" onClick={onCartClick}>
            <ShoppingCart className="icon" />
            {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
          </div>
          <button className="menu-btn" onClick={toggleMenu} aria-label="Toggle menu">
            {isMenuOpen ? <X /> : <Menu />}
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
