import React, { useState } from "react";
import "./Products.css";
import { ShoppingCart } from "lucide-react";

const Products = () => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("featured");

  const categories = [
    { id: "all", name: "All Products", count: 200 },
    { id: "electronics", name: "Electronics", count: 65 },
    { id: "clothing", name: "Clothing", count: 50 },
    { id: "home", name: "Home & Garden", count: 45 },
    { id: "sports", name: "Sports & Fitness", count: 25 },
    { id: "beauty", name: "Beauty & Health", count: 15 },
  ];

  // Convert USD to PKR (approximate conversion rate)
  const usdToPkr = (usd) => {
    const exchangeRate = 278;
    return Math.round(usd * exchangeRate).toLocaleString('en-PK');
  };

  // Product data generation with realistic items
  const generateProducts = () => {
    const electronics = [
      { name: "Wireless Headphones", basePrice: 50, category: "electronics" },
      { name: "Smart Watch", basePrice: 120, category: "electronics" },
      { name: "Bluetooth Speaker", basePrice: 40, category: "electronics" },
      { name: "Laptop", basePrice: 600, category: "electronics" },
      { name: "Tablet", basePrice: 250, category: "electronics" },
      { name: "Smartphone", basePrice: 300, category: "electronics" },
      { name: "TV", basePrice: 400, category: "electronics" },
      { name: "Camera", basePrice: 350, category: "electronics" },
    ];

    const clothing = [
      { name: "T-Shirt", basePrice: 15, category: "clothing" },
      { name: "Jeans", basePrice: 40, category: "clothing" },
      { name: "Jacket", basePrice: 60, category: "clothing" },
      { name: "Dress", basePrice: 45, category: "clothing" },
      { name: "Sneakers", basePrice: 70, category: "clothing" },
      { name: "Hat", basePrice: 20, category: "clothing" },
      { name: "Socks", basePrice: 10, category: "clothing" },
      { name: "Underwear", basePrice: 15, category: "clothing" },
    ];

    const home = [
      { name: "Coffee Maker", basePrice: 50, category: "home" },
      { name: "Blender", basePrice: 40, category: "home" },
      { name: "Knife Set", basePrice: 80, category: "home" },
      { name: "Bed Sheets", basePrice: 30, category: "home" },
      { name: "Pillow", basePrice: 20, category: "home" },
      { name: "Lamp", basePrice: 35, category: "home" },
      { name: "Chair", basePrice: 60, category: "home" },
      { name: "Table", basePrice: 100, category: "home" },
    ];

    const sports = [
      { name: "Yoga Mat", basePrice: 25, category: "sports" },
      { name: "Dumbbells", basePrice: 30, category: "sports" },
      { name: "Running Shoes", basePrice: 80, category: "sports" },
      { name: "Basketball", basePrice: 20, category: "sports" },
      { name: "Tennis Racket", basePrice: 50, category: "sports" },
      { name: "Bicycle", basePrice: 200, category: "sports" },
      { name: "Treadmill", basePrice: 300, category: "sports" },
      { name: "Swim Goggles", basePrice: 15, category: "sports" },
    ];

    const beauty = [
      { name: "Shampoo", basePrice: 10, category: "beauty" },
      { name: "Conditioner", basePrice: 12, category: "beauty" },
      { name: "Face Cream", basePrice: 20, category: "beauty" },
      { name: "Perfume", basePrice: 40, category: "beauty" },
      { name: "Lipstick", basePrice: 15, category: "beauty" },
      { name: "Makeup Kit", basePrice: 50, category: "beauty" },
      { name: "Hair Dryer", basePrice: 35, category: "beauty" },
      { name: "Electric Shaver", basePrice: 45, category: "beauty" },
    ];

    const allProducts = [...electronics, ...clothing, ...home, ...sports, ...beauty];
    const products = [];

    for (let i = 0; i < 200; i++) {
      const baseProduct = allProducts[i % allProducts.length];
      const variation = Math.floor(i / allProducts.length) + 1;
      const discount = Math.random() > 0.5;
      const originalPrice = baseProduct.basePrice + (Math.random() * 20);
      const discountAmount = discount ? Math.floor(originalPrice * (0.1 + Math.random() * 0.3)) : 0;

      products.push({
        id: i + 1,
        name: `${baseProduct.name} ${variation > 1 ? `v${variation}` : ''}`.trim(),
        price: Math.round(originalPrice - discountAmount),
        originalPrice: discount ? Math.round(originalPrice) : null,
        rating: (3.5 + Math.random() * 1.5).toFixed(1),
        reviews: Math.floor(Math.random() * 200) + 10,
        image: `https://source.unsplash.com/300x300/?${baseProduct.category},${baseProduct.name.replace(/\s+/g, '')},${i}`,
        category: baseProduct.category,
        inStock: Math.random() > 0.1,
      });
    }

    return products;
  };

  const products = generateProducts();

  const filteredProducts = products.filter((product) => {
    const matchCategory = selectedCategory === "all" || product.category === selectedCategory;
    const matchSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchCategory && matchSearch;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case "price-low": return a.price - b.price;
      case "price-high": return b.price - a.price;
      case "rating": return b.rating - a.rating;
      default: return 0;
    }
  });

  const handleAddToCart = (productId) => {
    console.log(`Product ${productId} added to cart`);
    // Add your cart logic here
  };

  return (
    <div className="main-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h3 className="sidebar-title">Categories</h3>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className={`sidebar-btn ${selectedCategory === cat.id ? "active" : ""}`}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.name}
            <span className="badge-count">{cat.count}</span>
          </button>
        ))}
      </aside>

      {/* Main Products Section */}
      <div className="products-section">
        <div className="topbar">
          <h2 className="results-count">Showing {sortedProducts.length} of {products.length} products</h2>
          <div className="controls">
            <input
              type="text"
              className="search-bar"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <select 
              className="sort-dropdown"
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
            >
              <option value="featured">Featured</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Top Rated</option>
            </select>
          </div>
        </div>

        <div className="product-grid">
          {sortedProducts.map((product) => (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                <img src={product.image} alt={product.name} className="product-img" />
              </div>
              <div className="product-details">
                <div className="rating">
                  <span className="stars">{"★".repeat(Math.floor(product.rating))}</span>
                  <span className="review-count">({product.reviews})</span>
                </div>
                <h4 className="product-name">{product.name}</h4>
                <div className="price-section">
                  <span className="price">Rs. {usdToPkr(product.price)}</span>
                  {product.originalPrice && (
                    <span className="original-price">Rs. {usdToPkr(product.originalPrice)}</span>
                  )}
                </div>
                {product.inStock && (
                  <button 
                    className="action-btn" 
                    onClick={() => handleAddToCart(product.id)}
                  >
                    <ShoppingCart size={16} /> Add to Cart
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Products;
