// src/pages/Categories.js
import React from "react";
import { Link } from "react-router-dom";
import Header from "../components/Header";
import { 
  Headphones, 
  Shirt, 
  Home, 
  Dumbbell, 
  Spa, 
  Watch, 
  Gamepad2, 
  Car, 
  Book, 
  Baby 
} from "lucide-react";
import "./Categories.css";

const Categories = () => {
  const categories = [
    {
      id: 1,
      name: "Electronics",
      description: "Smartphones, laptops, tablets and more",
      icon: Headphones,
      productCount: 15,
      color: "blue",
    },
    {
      id: 2,
      name: "Clothing",
      description: "Fashion for men, women and kids",
      icon: Shirt,
      productCount: 12,
      color: "purple",
    },
    {
      id: 3,
      name: "Home & Garden",
      description: "Furniture, decor and outdoor items",
      icon: Home,
      productCount: 10,
      color: "green",
    },
    {
      id: 4,
      name: "Sports & Fitness",
      description: "Equipment and athletic wear",
      icon: Dumbbell,
      productCount: 8,
      color: "red",
    },
    {
      id: 5,
      name: "Beauty & Healthy",
      description: "Premium skincare, cosmetics, and wellness products",
      icon: Spa,
      productCount: 5,
      color: "indigo",
    },
    {
      id: 6,
      name: "Watches",
      description: "Elegant accessories and timepieces",
      icon: Watch,
      productCount: 7,
      color: "teal",
    },
    {
      id: 7,
      name: "Gaming",
      description: "Consoles, games and accessories",
      icon: Gamepad2,
      productCount: 9,
      color: "orange",
    },
    {
      id: 8,
      name: "Automotive",
      description: "Car accessories and maintenance",
      icon: Car,
      productCount: 6,
      color: "yellow",
    },
    {
      id: 9,
      name: "Books",
      description: "Fiction, non-fiction and educational",
      icon: Book,
      productCount: 11,
      color: "brown",
    },
    {
      id: 10,
      name: "Baby Care",
      description: "Essentials for your little ones",
      icon: Baby,
      productCount: 4,
      color: "pink",
    }
  ];

  return (
    <>
      <Header /> 
      <div className="categories-page">
        <header className="header">Shop by Category</header>
        <p className="description">
          Discover our wide range of product categories and find exactly what you're looking for.
        </p>

        <div className="grid">
          {categories.map((category) => {
            const Icon = category.icon;
            return (
              <Link
                key={category.id}
                to={`/products?category=${category.name.toLowerCase()}`}
                className="category-card"
              >
                <div className={`icon-circle ${category.color}`}>
                  <Icon size={32} />
                </div>
                <h3>{category.name}</h3>
                <p className="text">{category.description}</p>
                <span className="badge">{category.productCount} products</span>
              </Link>
            );
          })}
        </div>

        <div className="banner">
          <h2>Can't find what you're looking for?</h2>
          <p>Browse all our products or use our search feature to find exactly what you need.</p>
          <Link to="/products" className="view-all-btn">
            View All Products
          </Link>
        </div>
      </div>
    </>
  );
};

export default Categories;
