import React, { useState } from "react";
import "./Products.css";
import { ShoppingCart } from "lucide-react";

const Products = () => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("featured");

  const categories = [
    { id: "all", name: "All Products", count: 8 },
    { id: "electronics", name: "Electronics", count: 3 },
    { id: "clothing", name: "Clothing", count: 2 },
    { id: "home", name: "Home & Garden", count: 2 },
    { id: "sports", name: "Sports & Fitness", count: 1 },
  ];

  // Convert USD to PKR (approximate conversion rate)
  const usdToPkr = (usd) => {
    const exchangeRate = 278;
    return Math.round(usd * exchangeRate).toLocaleString('en-PK');
  };

  const products = [
    {
      id: 1,
      name: "Wireless Headphones Pro",
      price: 99,
      originalPrice: 129,
      rating: 4.5,
      reviews: 215,
      image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=300&fit=crop",
      category: "electronics",
      inStock: true,
    },
    {
      id: 2,
      name: "Smart Watch X3",
      price: 199,
      originalPrice: 249,
      rating: 4.2,
      reviews: 189,
      image: "https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=300&h=300&fit=crop",
      category: "electronics",
      inStock: true,
    },
    {
      id: 3,
      name: "Premium Coffee Maker",
      price: 89,
      originalPrice: null,
      rating: 4.7,
      reviews: 156,
      image: "https://images.unsplash.com/photo-1550583724-b2692b85b150?w=300&h=300&fit=crop",
      category: "home",
      inStock: true,
    },
    {
      id: 4,
      name: "Leather Messenger Bag",
      price: 79,
      originalPrice: 99,
      rating: 4.3,
      reviews: 98,
      image: "https://images.unsplash.com/photo-1590874103328-eac38a683ce7?w=300&h=300&fit=crop",
      category: "clothing",
      inStock: true,
    },
    {
      id: 5,
      name: "Bluetooth Speaker Mini",
      price: 49,
      originalPrice: null,
      rating: 4.0,
      reviews: 132,
      image: "https://images.unsplash.com/photo-1572569511254-d8f925fe2cbb?w=300&h=300&fit=crop",
      category: "electronics",
      inStock: true,
    },
    {
      id: 6,
      name: "Professional Yoga Mat",
      price: 29,
      originalPrice: 39,
      rating: 4.4,
      reviews: 87,
      image: "https://images.unsplash.com/photo-1545205597-3d9d02c29597?w=300&h=300&fit=crop",
      category: "sports",
      inStock: true,
    },
    {
      id: 7,
      name: "Chef's Knife Set",
      price: 129,
      originalPrice: 159,
      rating: 4.8,
      reviews: 204,
      image: "https://images.unsplash.com/photo-1583947581924-a6d4a7e75ad9?w=300&h=300&fit=crop",
      category: "home",
      inStock: true,
    },
    {
      id: 8,
      name: "Running Shoes AirMax",
      price: 119,
      originalPrice: null,
      rating: 4.6,
      reviews: 176,
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=300&h=300&fit=crop",
      category: "clothing",
      inStock: true,
    },
  ];

  const filteredProducts = products.filter((product) => {
    const matchCategory = selectedCategory === "all" || product.category === selectedCategory;
    const matchSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchCategory && matchSearch;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case "price-low": return a.price - b.price;
      case "price-high": return b.price - a.price;
      case "rating": return b.rating - a.rating;
      default: return 0;
    }
  });

  const handleAddToCart = (productId) => {
    console.log(`Product ${productId} added to cart`);
    // Add your cart logic here
  };

  return (
    <div className="main-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h3 className="sidebar-title">Categories</h3>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className={`sidebar-btn ${selectedCategory === cat.id ? "active" : ""}`}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.name}
            <span className="badge-count">{cat.count}</span>
          </button>
        ))}
      </aside>

      {/* Main Products Section */}
      <div className="products-section">
        <div className="topbar">
          <h2 className="results-count">Showing {sortedProducts.length} of {products.length} products</h2>
          <div className="controls">
            <input
              type="text"
              className="search-bar"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <select 
              className="sort-dropdown"
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
            >
              <option value="featured">Featured</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Top Rated</option>
            </select>
          </div>
        </div>

        <div className="product-grid">
          {sortedProducts.map((product) => (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                <img src={product.image} alt={product.name} className="product-img" />
              </div>
              <div className="product-details">
                <div className="rating">
                  <span className="stars">{"★".repeat(Math.floor(product.rating))}</span>
                  <span className="review-count">({product.reviews})</span>
                </div>
                <h4 className="product-name">{product.name}</h4>
                <div className="price-section">
                  <span className="price">Rs. {usdToPkr(product.price)}</span>
                  {product.originalPrice && (
                    <span className="original-price">Rs. {usdToPkr(product.originalPrice)}</span>
                  )}
                </div>
                {product.inStock && (
                  <button 
                    className="action-btn" 
                    onClick={() => handleAddToCart(product.id)}
                  >
                    <ShoppingCart size={16} /> Add to Cart
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Products;
