import React, { useState, useEffect } from "react";
import "./Products.css";
import { ShoppingCart, Bell, Star, Filter, Search } from "lucide-react";

const Products = () => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("featured");
  const [priceRange, setPriceRange] = useState([0, 200]);
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState(false);
  const [selectedBadges, setSelectedBadges] = useState([]);

  const categories = [
    { id: "all", name: "All Products", count: 120 },
    { id: "electronics", name: "Electronics", count: 35 },
    { id: "clothing", name: "Clothing", count: 28 },
    { id: "home", name: "Home & Garden", count: 22 },
    { id: "sports", name: "Sports & Fitness", count: 18 },
    { id: "beauty", name: "Beauty & Health", count: 17 },
  ];

  const productNames = [
    "Wireless Headphones", "Smart Watch", "Coffee Maker", "Messenger Bag",
    "Bluetooth Speaker", "Yoga Mat", "Knife Set", "Running Shoes",
    "Wireless Earbuds", "Fitness Tracker", "Air Fryer", "Backpack",
    "Portable Charger", "Resistance Bands", "Cookware Set", "Sneakers"
  ];

  const categoryTypes = ["electronics", "clothing", "home", "sports", "beauty"];
  const badges = ["Best Seller", "New", "Sale", "Popular", "Limited"];
  const allBadges = [...new Set(badges)];

  const generateProducts = () =>
    Array.from({ length: 120 }, (_, i) => {
      const category = categoryTypes[i % categoryTypes.length];
      const discount = Math.random() > 0.5;
      const originalPrice = Math.floor(Math.random() * 180) + 20;
      const discountAmount = discount ? Math.floor(originalPrice * (0.1 + Math.random() * 0.4)) : 0;

      return {
        id: i + 1,
        name: productNames[i % productNames.length] + " " + (i % 8 + 1),
        price: originalPrice - discountAmount,
        originalPrice: discount ? originalPrice : null,
        rating: (Math.random() * 2 + 3).toFixed(1),
        reviews: Math.floor(Math.random() * 200) + 10,
        image: `https://source.unsplash.com/300x300/?${category},${productNames[i % productNames.length].toLowerCase().replace(" ", "")}`,
        badge: badges[Math.floor(Math.random() * badges.length)],
        category,
        inStock: Math.random() > 0.1,
        fastDelivery: Math.random() > 0.7,
        ecoFriendly: Math.random() > 0.5,
      };
    });

  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Simulate loading products
    setProducts(generateProducts());
  }, []);

  const filteredProducts = products.filter((product) => {
    const matchCategory = selectedCategory === "all" || product.category === selectedCategory;
    const matchSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
    const matchBadges = selectedBadges.length === 0 || selectedBadges.includes(product.badge);
    
    return matchCategory && matchSearch && matchPrice && matchBadges;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case "price-low": return a.price - b.price;
      case "price-high": return b.price - a.price;
      case "rating": return b.rating - a.rating;
      case "newest": return b.id - a.id;
      default: return 0;
    }
  });

  const handlePriceChange = (e, index) => {
    const newPriceRange = [...priceRange];
    newPriceRange[index] = parseInt(e.target.value);
    setPriceRange(newPriceRange);
  };

  const toggleBadge = (badge) => {
    setSelectedBadges(prev => 
      prev.includes(badge) 
        ? prev.filter(b => b !== badge) 
        : [...prev, badge]
    );
  };

  return (
    <div className="main-container">
      {/* Mobile Filter Button */}
      <button 
        className="mobile-filter-btn"
        onClick={() => setMobileFiltersOpen(true)}
      >
        <Filter size={16} /> Filters
      </button>

      {/* Mobile Filters Overlay */}
      {mobileFiltersOpen && (
        <div className="mobile-filters-overlay">
          <div className="mobile-filters-content">
            <div className="mobile-filters-header">
              <h3>Filters</h3>
              <button onClick={() => setMobileFiltersOpen(false)}>Ã—</button>
            </div>
            
            <div className="filter-section">
              <h4>Categories</h4>
              {categories.map((cat) => (
                <button
                  key={cat.id}
                  className={`filter-btn ${selectedCategory === cat.id ? "active" : ""}`}
                  onClick={() => setSelectedCategory(cat.id)}
                >
                  {cat.name}
                  <span className="badge-count">{cat.count}</span>
                </button>
              ))}
            </div>

            <div className="filter-section">
              <h4>Price Range</h4>
              <div className="price-slider">
                <span>${priceRange[0]}</span>
                <input
                  type="range"
                  min="0"
                  max="200"
                  value={priceRange[0]}
                  onChange={(e) => handlePriceChange(e, 0)}
                />
                <input
                  type="range"
                  min="0"
                  max="200"
                  value={priceRange[1]}
                  onChange={(e) => handlePriceChange(e, 1)}
                />
                <span>${priceRange[1]}</span>
              </div>
            </div>

            <div className="filter-section">
              <h4>Tags</h4>
              <div className="badge-filters">
                {allBadges.map(badge => (
                  <button
                    key={badge}
                    className={`badge-filter ${selectedBadges.includes(badge) ? 'active' : ''}`}
                    onClick={() => toggleBadge(badge)}
                  >
                    {badge}
                  </button>
                ))}
              </div>
            </div>

            <button 
              className="apply-filters-btn"
              onClick={() => setMobileFiltersOpen(false)}
            >
              Apply Filters
            </button>
          </div>
        </div>
      )}

      {/* Sidebar */}
      <aside className="sidebar">
        <h3>Categories</h3>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className={`sidebar-btn ${selectedCategory === cat.id ? "active" : ""}`}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.name}
            <span className="badge-count">{cat.count}</span>
          </button>
        ))}

        <div className="filter-section">
          <h3>Price Range</h3>
          <div className="price-slider">
            <span>${priceRange[0]}</span>
            <input
              type="range"
              min="0"
              max="200"
              value={priceRange[0]}
              onChange={(e) => handlePriceChange(e, 0)}
            />
            <input
              type="range"
              min="0"
              max="200"
              value={priceRange[1]}
              onChange={(e) => handlePriceChange(e, 1)}
            />
            <span>${priceRange[1]}</span>
          </div>
        </div>

        <div className="filter-section">
          <h3>Tags</h3>
          <div className="badge-filters">
            {allBadges.map(badge => (
              <button
                key={badge}
                className={`badge-filter ${selectedBadges.includes(badge) ? 'active' : ''}`}
                onClick={() => toggleBadge(badge)}
              >
                {badge}
              </button>
            ))}
          </div>
        </div>
      </aside>

      {/* Main Products Section */}
      <div className="products-section">
        <div className="topbar">
          <h2>Showing {sortedProducts.length} of {products.length} products</h2>
          
          <div className="search-container">
            <Search size={18} className="search-icon" />
            <input
              type="text"
              className="search-bar"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          
          <div className="sort-container">
            <label>Sort by:</label>
            <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
              <option value="featured">Featured</option>
              <option value="newest">Newest</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Top Rated</option>
            </select>
          </div>
        </div>

        {sortedProducts.length === 0 ? (
          <div className="no-results">
            <h3>No products found</h3>
            <p>Try adjusting your filters or search term</p>
            <button 
              className="reset-filters-btn"
              onClick={() => {
                setSelectedCategory("all");
                setSearchQuery("");
                setPriceRange([0, 200]);
                setSelectedBadges([]);
              }}
            >
              Reset All Filters
            </button>
          </div>
        ) : (
          <div className="product-grid">
            {sortedProducts.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-image-container">
                  {product.badge && (
                    <span className={`product-badge ${product.badge.toLowerCase().replace(" ", "-")}`}>
                      {product.badge}
                    </span>
                  )}
                  <img 
                    src={product.image} 
                    alt={product.name} 
                    className="product-img" 
                    loading="lazy"
                  />
                  {product.fastDelivery && (
                    <span className="delivery-badge">Fast Delivery</span>
                  )}
                  {product.ecoFriendly && (
                    <span className="eco-badge">Eco Friendly</span>
                  )}
                </div>
                
                <div className="product-details">
                  <div className="rating">
                    <Star size={16} fill="#ffc107" color="#ffc107" />
                    <span>{product.rating}</span>
                    <span className="review-count">({product.reviews})</span>
                  </div>
                  <h4>{product.name}</h4>
                  <div className="price-section">
                    <span className="price">${product.price.toFixed(2)}</span>
                    {product.originalPrice && (
                      <span className="original-price">${product.originalPrice.toFixed(2)}</span>
                    )}
                  </div>
                  <button 
                    className={`action-btn ${product.inStock ? "" : "out-stock"}`} 
                    disabled={!product.inStock}
                  >
                    {product.inStock ? (
                      <>
                        <ShoppingCart size={16} /> Add to Cart
                      </>
                    ) : (
                      <>
                        <Bell size={16} /> Notify Me
                      </>
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {sortedProducts.length > 0 && (
          <div className="pagination">
            <button disabled>Previous</button>
            <button className="active">1</button>
            <button>2</button>
            <button>3</button>
            <button>Next</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
