import React, { useState } from "react";
import { ShoppingCart, Search, User, Heart, Menu, X } from "lucide-react";
import "./Header.css";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [itemCount, setItemCount] = useState(2); // dummy cart count
  const [isCartOpen, setIsCartOpen] = useState(false); // just for mock

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo */}
        <div className="logo">
          <span className="green">Buy</span><span className="black">Now</span>
        </div>

        {/* Navigation */}
        <nav className="nav">
          <a href="/">Home</a>
          <a href="/products">Product</a>
          <a href="/categories">Categories</a>
          <a href="/about">About</a>
          <a href="/contact">Contact</a>
        </nav>

        {/* Search */}
        <div className="search-bar">
          <Search className="search-icon" />
          <input type="text" placeholder="Search Products....." />
        </div>

        {/* Icons */}
        <div className="icons">
          <Heart className="icon" />
          <a href="/login">
            <User className="icon" />
          </a>
          <div className="cart-wrapper" onClick={() => setIsCartOpen(true)}>
            <ShoppingCart className="icon" />
            {itemCount > 0 && <span className="cart-badge">{itemCount}</span>}
          </div>
          <button className="menu-btn" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            {isMenuOpen ? <X /> : <Menu />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="mobile-menu">
          <input type="text" placeholder="Search Products....." />
          <a href="/">Home</a>
          <a href="/products">Product</a>
          <a href="/categories">Categories</a>
          <a href="/about">About</a>
          <a href="/contact">Contact</a>
        </div>
      )}
    </header>
  );
};

export default Header;
