import React, { useState } from "react";
import "./Products.css";
import { ShoppingCart, Bell } from "lucide-react";

const Products = () => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("featured");

  const categories = [
    { id: "all", name: "All Products", count: 120 },
    { id: "electronics", name: "Electronics", count: 35 },
    { id: "clothing", name: "Clothing", count: 28 },
    { id: "home", name: "Home & Garden", count: 22 },
    { id: "sports", name: "Sports & Fitness", count: 18 },
    { id: "beauty", name: "Beauty & Health", count: 17 },
  ];

  const productNames = [
    "Wireless Headphones", "Smart Watch", "Coffee Maker", "Messenger Bag",
    "Bluetooth Speaker", "Yoga Mat", "Knife Set", "Running Shoes"
  ];

  const categoryTypes = ["electronics", "clothing", "home", "sports", "beauty"];

  // Convert USD to PKR (approximate conversion rate)
  const usdToPkr = (usd) => {
    const exchangeRate = 278; // Current approximate rate
    return Math.round(usd * exchangeRate).toLocaleString('en-PK');
  };

  const generateProducts = () =>
    Array.from({ length: 120 }, (_, i) => {
      const category = categoryTypes[i % categoryTypes.length];
      const discount = Math.random() > 0.5;
      const originalPrice = Math.floor(Math.random() * 150) + 100;
      const discountAmount = discount ? Math.floor(originalPrice * 0.3) : 0;

      return {
        id: i + 1,
        name: productNames[i % productNames.length] + " " + (i + 1),
        price: originalPrice - discountAmount,
        originalPrice: discount ? originalPrice : null,
        rating: (Math.random() * 2 + 3).toFixed(1),
        reviews: Math.floor(Math.random() * 200) + 10,
        image: `https://source.unsplash.com/300x300/?${category},${i + 1}`,
        category,
        inStock: Math.random() > 0.1,
      };
    });

  const products = generateProducts();

  const filteredProducts = products.filter((product) => {
    const matchCategory = selectedCategory === "all" || product.category === selectedCategory;
    const matchSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchCategory && matchSearch;
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case "price-low": return a.price - b.price;
      case "price-high": return b.price - a.price;
      case "rating": return b.rating - a.rating;
      default: return 0;
    }
  });

  return (
    <div className="main-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h3 className="sidebar-title">Categories</h3>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className={`sidebar-btn ${selectedCategory === cat.id ? "active" : ""}`}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.name}
            <span className="badge-count">{cat.count}</span>
          </button>
        ))}
      </aside>

      {/* Main Products Section */}
      <div className="products-section">
        <div className="topbar">
          <h2 className="results-count">Showing {sortedProducts.length} of 120 products</h2>
          <div className="controls">
            <input
              type="text"
              className="search-bar"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <select 
              className="sort-dropdown"
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
            >
              <option value="featured">Featured</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="rating">Top Rated</option>
            </select>
          </div>
        </div>

        <div className="product-grid">
          {sortedProducts.map((product) => (
            <div key={product.id} className="product-card">
              {product.badge && <span className={`product-badge ${product.badge.toLowerCase().replace(" ", "-")}`}>{product.badge}</span>}
              <div className="product-image-container">
                <img src={product.image} alt={product.name} className="product-img" />
              </div>
              <div className="product-details">
                <div className="rating">
                  <span className="stars">{"★".repeat(Math.floor(product.rating))}</span>
                  <span className="review-count">({product.reviews})</span>
                </div>
                <h4 className="product-name">{product.name}</h4>
                <div className="price-section">
                  <span className="price">Rs. {usdToPkr(product.price)}</span>
                  {product.originalPrice && (
                    <span className="original-price">Rs. {usdToPkr(product.originalPrice)}</span>
                  )}
                </div>
                <button className={`action-btn ${product.inStock ? "" : "out-stock"}`} disabled={!product.inStock}>
                  {product.inStock ? (
                    <>
                      <ShoppingCart size={16} /> Add to Cart
                    </>
                  ) : (
                    <>
                                          <ShoppingCart size={16} /> Add to Cart

                    </>
                  )}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Products;
