import React from "react";
import { ArrowRight, Heart, ShoppingCart, Star, Shield, Truck, RotateCcw, Headphones, Award } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useCart } from "@/context/CartContext";
import { toast } from "@/hooks/use-toast";

const products = [
  {
    id: 1,
    name: "Premium Wireless Headphones",
    price: 299,
    originalPrice: 399,
    rating: 4.8,
    reviews: 124,
    image: "https://images.unsplash.com/photo-1604671805440-5d0a842649c3?w=600",
    inStock: true
  },
  {
    id: 2,
    name: "Smart Fitness Watch",
    price: 199,
    originalPrice: 249,
    rating: 4.6,
    reviews: 89,
    image: "https://images.unsplash.com/photo-1585386959984-a41552261f66?w=600",
    inStock: true
  },
  {
    id: 3,
    name: "Leather Messenger Bag",
    price: 89,
    originalPrice: 129,
    rating: 4.7,
    reviews: 156,
    image: "https://images.unsplash.com/photo-1600181953606-9c0d9d7841f4?w=600",
    inStock: true
  }
];

const HomePage = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (product: any) => {
    if (!product.inStock) return;
    addToCart(product);
    toast({
      title: "Added to cart!",
      description: `${product.name} has been added to your cart.`,
    });
  };

  return (
    <>
      {/* HERO SECTION */}
      <section className="relative min-h-[80vh] flex items-center justify-center text-center bg-green-800 bg-cover bg-center text-white px-4" style={{ backgroundImage: `url('https://images.unsplash.com/photo-1592878849120-fd9c0b23b3d7?w=1200')` }}>
        <div className="bg-green-900/80 w-full h-full absolute inset-0 z-0" />
        <div className="relative z-10">
          <h1 className="text-5xl md:text-7xl font-bold mb-4">SUMMER SALE</h1>
          <p className="text-2xl font-semibold mb-8">Up to 50% Off Everything!</p>
          <div className="flex flex-wrap justify-center gap-4 mb-6">
            <span className="bg-gray-200 text-black font-bold px-6 py-3 rounded-lg">Limited Time Offer</span>
            <span className="bg-gray-200 text-black font-bold px-6 py-3 rounded-lg">Free Shipping</span>
            <span className="bg-gray-200 text-black font-bold px-6 py-3 rounded-lg">Best Deals</span>
          </div>
          <Button size="lg" className="bg-white text-black font-bold text-xl px-8 py-4 hover:bg-gray-200">
            Shop Now
            <ArrowRight className="ml-2" />
          </Button>
        </div>
      </section>

      {/* TRENDING PRODUCTS */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-4xl font-bold mb-4">Top Picks of the Season</h2>
          <p className="text-muted-foreground mb-10 text-lg">Browse our trending selections at discounted prices</p>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {products.map((product) => (
              <Card key={product.id} className="shadow hover:shadow-lg transition duration-300">
                <img src={product.image} alt={product.name} className="h-64 w-full object-cover" />
                <CardContent className="p-4 text-left">
                  <div className="flex items-center mb-2">
                    {[...Array(5)].map((_, i) => (
                      <Star key={i} className={`h-4 w-4 ${i < Math.floor(product.rating) ? 'text-yellow-500 fill-current' : 'text-gray-300'}`} />
                    ))}
                    <span className="ml-2 text-sm text-gray-500">({product.reviews})</span>
                  </div>
                  <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
                  <div className="flex justify-between items-center mb-4">
                    <span className="text-xl font-bold text-green-600">${product.price}</span>
                    {product.originalPrice > product.price && (
                      <span className="text-sm line-through text-gray-400">${product.originalPrice}</span>
                    )}
                  </div>
                  <Button className="w-full" onClick={() => handleAddToCart(product)}>
                    <ShoppingCart className="h-4 w-4 mr-2" />
                    Add to Cart
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </section>

      {/* TRUST SECTION */}
      <section className="py-20 bg-gray-100">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12">Why Shop With Us</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 text-center">
            {[{
              icon: Shield,
              title: "Secure Payments",
              desc: "256-bit SSL encryption protects all transactions"
            }, {
              icon: Truck,
              title: "Free Shipping",
              desc: "Free delivery on orders over $50"
            }, {
              icon: RotateCcw,
              title: "Easy Returns",
              desc: "30-day hassle-free return policy"
            }, {
              icon: Headphones,
              title: "24/7 Support",
              desc: "Round-the-clock customer service"
            }].map((item, i) => (
              <Card key={i} className="shadow">
                <CardContent className="p-6">
                  <div className="flex justify-center mb-4">
                    <item.icon className="h-10 w-10 text-green-600" />
                  </div>
                  <h4 className="font-semibold text-lg mb-2">{item.title}</h4>
                  <p className="text-gray-600 text-sm">{item.desc}</p>
                </CardContent>
              </Card>
            ))}
          </div>

          <div className="text-center mt-16">
            <Award className="mx-auto text-green-600 mb-4" />
            <h3 className="text-2xl font-bold mb-2">Trusted by Thousands</h3>
            <p className="text-gray-600 max-w-xl mx-auto mb-4">
              Over <strong>50K+</strong> happy customers, <strong>100K+</strong> products sold, and a <strong>4.9/5</strong> average rating.
            </p>
          </div>
        </div>
      </section>
    </>
  );
};

export default HomePage;
